"use strict";
const stylesItem = ":where(li) {\n  /**\n   * CAUTION need at least 1px height to scroll to \n   * the active item on initial render (startAt)\n   */\n  min-height: 1px;\n  /*  */\n  scroll-snap-align: start;\n  flex-shrink: 0;\n  display: flex;\n  align-items: center;\n  justify-content: center;\n  width: auto;\n  max-width: 100%;\n  height: 100%;\n}\n\n:where(input[type='radio']) {\n  appearance: none;\n}\n\n:where(li[aria-current='true']) {\n  outline: 0.15em solid;\n  outline-offset: 2px;\n  border-radius: 1px;\n  z-index: 1;\n}\n";
module.exports = stylesItem;
