"use strict";
const styles = "div {\n  display: grid;\n  grid-template-columns: 1.5em auto;\n  grid-template-rows: 1.5em;\n  column-gap: 0.25ch;\n}\n\nbutton {\n  grid-column: 1 / 1;\n  grid-row: 1;\n  pointer-events: none;\n}\n\nselect,\nspan {\n  /* position */\n  position: relative;\n  z-index: 1;\n  grid-column: 1 / 1;\n  grid-row: 1;\n  /*  */\n  cursor: pointer;\n}\n\nspan {\n  z-index: 0;\n  position: relative;\n  display: flex;\n  justify-content: center;\n}\n\nselect:not(:focus) {\n  /* reset all to hide */\n  background-color: transparent;\n  color: transparent;\n  border-color: transparent;\n}\n\nselect:focus {\n  grid-column: 1 / 3;\n}\n\ninput {\n  grid-column: 2 / 3;\n  grid-row: 1;\n}\n";
module.exports = styles;
