"use strict";
const styles = "*[data-state] {\n  z-index: 99;\n  position: absolute;\n  opacity: 0;\n}\n\n*[data-state='positioned'] {\n  display: block;\n  top: var(--y, 0px);\n  left: var(--x, 0px);\n  transition: opacity var(--duration) linear;\n  opacity: 1;\n}\n\n*[data-state='unpositioned'] {\n  display: block;\n  visibility: 'hidden';\n  opacity: 0;\n}\n\n*[data-state='closing'] {\n  top: var(--y, 0px);\n  left: var(--x, 0px);\n  transition: opacity var(--duration) linear;\n  transition-delay: var(--duration) ms;\n  opacity: 0;\n}\n\n*[data-state='hidden'] {\n  display: none;\n}\n";
module.exports = styles;
