import{MeiliSearch as a}from"meilisearch";const t=new a({host:"http://localhost:7700",apiKey:process.env.MEILISEARCH_MASTER_KEY});(async()=>{try{await t.createIndex("lecturers",{primaryKey:"uuid"})}catch{}await t.index("lecturers").updateSettings({filterableAttributes:["tags.uuid","tags.alias","route_url","location","price_per_hour"],sortableAttributes:["first_name","last_name","uuid","price_per_hour"]});try{await t.createIndex("tags",{primaryKey:"uuid"})}catch{}await t.index("tags").updateSettings({filterableAttributes:["name"],sortableAttributes:["name"]})})();
